import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import './Navigation.css';

function Navigation() {
  let location = useLocation();
  let navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('userName'); // Optionally clear user-related storage
    navigate('/signin'); // Navigate to the sign-in page
  };

  const speakWithDelay = (text) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      speechSynthesis.cancel(); // Clear any previously scheduled speech synthesis
      setTimeout(() => {
        speechSynthesis.speak(utterance); // Delayed speech synthesis
      }, 100);
    } else {
      console.error("Your browser does not support speech synthesis.");
    }
  };

  return (
    <div className="navigation-container">
      {/* Conditionally render sign up and sign in links if not on their respective pages or welcome page */}
      {location.pathname !== '/welcome' && location.pathname !== '/signin' && location.pathname !== '/signup' && (
        <nav>
          <ul>
            <li>
              <Link
                to="/signup"
                onMouseEnter={() => speakWithDelay("Go to sign up")}
                onClick={() => speakWithDelay("You are in sign up")}
                aria-label="Sign up for a new account"
                role="link"
                style={{ color: 'white'}}
              >
                SIGN UP
              </Link>
            </li>
            <li>
              <Link
                to="/signin"
                onMouseEnter={() => speakWithDelay("Go to sign in")}
                onClick={() => speakWithDelay("You are in sign in")}
                aria-label="Sign in to your account"
                role="link"
                style={{ color: 'white'}}
              >
                SIGN IN
              </Link>
            </li>
          </ul>
        </nav>
      )}
      {/* Always render logout button except on sign in and sign up pages */}
      {location.pathname !== '/signin' && location.pathname !== '/signup' && (
        <button onClick={handleLogout} className="logout-button">Logout</button>
      )}
    </div>
  );
}

export default Navigation;
