import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from './AuthContext'; // Adjust the path as necessary
import './Navigation.css';

function Navigation() {
  let location = useLocation();
  let navigate = useNavigate();
  const isLoggedIn = Boolean(localStorage.getItem('userName'));

  const speakWithDelay = (text) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      speechSynthesis.cancel(); // Clear any previously scheduled speech synthesis
      setTimeout(() => {
        speechSynthesis.speak(utterance); // Delayed speech synthesis
      }, 100);
    } else {
      console.error("Your browser does not support speech synthesis.");
    }
  };

  // Assume we use local storage to track if the user is 'logged in'

  const handleLogout = () => {
    speakWithDelay("Logging out"); // Provide audible feedback
    localStorage.removeItem('userName'); // Clear user-related storage indicating logged out
    navigate('/signin'); // Navigate to the sign-in page
  };

  return (
    <div className="navigation-container">
      if (isLoggedIn) {
        <button onClick={handleLogout} className="logout-button" style={{ position: 'absolute', right: '20px', top: '20px', backgroundColor: 'red', color: 'white', padding: '10px', fontSize: '18px' }} aria-label="Log out">
          Logout
        </button>}
        else{
        <nav>
          <ul>
            <li>
              <Link
                to="/signup"
                onMouseEnter={() => speakWithDelay("Go to sign up")}
                onClick={() => speakWithDelay("You are in sign up")}
                onFocus={() => speakWithDelay("Go to sign up.")}
                aria-label="Sign up for a new account"
                role="link"
                style={{ color: 'white'}}
              >
                SIGN UP
              </Link>
            </li>
            <li>
              <Link
                to="/signin"
                onMouseEnter={() => speakWithDelay("Go to sign in")}
                onClick={() => speakWithDelay("You are in sign in")}
                onFocus={() => speakWithDelay("Go to sign in.")}
                aria-label="Sign in to your account"
                role="link"
                style={{ color: 'white'}}
              >
                SIGN IN
              </Link>
            </li>
          </ul>
        </nav>}
    </div>
  );
}

export default Navigation;
