import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import './Navigation.css';

function Navigation() {
  const location = useLocation();
  const navigate = useNavigate();

  // Function to speak text with delay
  const speakWithDelay = (text) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      speechSynthesis.cancel();
      setTimeout(() => {
        speechSynthesis.speak(utterance);
      }, 100);
    } else {
      console.error("Your browser does not support speech synthesis.");
    }
  };

  // Function to handle logout
  const handleLogout = () => {
    speakWithDelay("Logging out");
    localStorage.removeItem('userName'); // Remove the userName from localStorage
    navigate('/signin'); // Navigate to sign-in page
  };

  // Check if user is logged in
  const isLoggedIn = Boolean(localStorage.getItem('userName'));

  return (
    <div className="navigation-container">
      {isLoggedIn && (
        // Show the logout button if the user is logged in
        <button onClick={handleLogout} className="logout-button" style={{ position: 'absolute', right: '20px', top: '20px', backgroundColor: 'red', color: 'white', padding: '10px 20px', fontSize: '1rem', cursor: 'pointer' }} aria-label="Log out" onMouseEnter={() => speakWithDelay("Log out")} onFocus={() => speakWithDelay("Log out")}>
          Logout
        </button>
      )}
      {!isLoggedIn && (
        // Show navigation links if the user is not logged in
        <nav>
          <ul>
            <li>
              <Link to="/signup" onMouseEnter={() => speakWithDelay("Go to sign up")} onClick={() => speakWithDelay("You are in sign up")} aria-label="Sign up for a new account" role="link">
                SIGN UP
              </Link>
            </li>
            <li>
              <Link to="/signin" onMouseEnter={() => speakWithDelay("Go to sign in")} onClick={() => speakWithDelay("You are in sign in")} aria-label="Sign in to your account" role="link">
                SIGN IN
              </Link>
            </li>
          </ul>
        </nav>
      )}
    </div>
  );
}

export default Navigation;
