import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import './Navigation.css';

function Navigation() {
  const location = useLocation();
  const navigate = useNavigate();
  const isLoggedIn = Boolean(localStorage.getItem('userName'));

  const speakWithDelay = (text) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.rate = 0.8; // Optionally adjust the rate of speech
      speechSynthesis.cancel();
      setTimeout(() => {
        speechSynthesis.speak(utterance);
      }, 100);
    } else {
      console.error("Your browser does not support speech synthesis.");
    }
  };

  const handleLogout = () => {
    speakWithDelay("Logging out");
    localStorage.removeItem('userName');
    navigate('/signin');
  };

  return (
    <div className="navigation-container" style={{ position: 'relative' }}>
      {isLoggedIn ? (
        <button
          onClick={handleLogout}
          className="logout-button"
          style={{
            position: 'absolute', right: '20px', top: '20px', 
            backgroundColor: 'red', color: 'white', 
            padding: '10px', fontSize: '18px', cursor: 'pointer'
          }}
          aria-label="Log out"
          onMouseEnter={() => speakWithDelay("Log out")}
          onFocus={() => speakWithDelay("Log out")}
        >
          Logout
        </button>
      ) : (
        <nav>
          <ul>
            <li>
              <Link to="/signup" onMouseEnter={() => speakWithDelay("Go to sign up")} onClick={() => speakWithDelay("You are in sign up")} aria-label="Sign up for a new account" role="link" style={{ color: 'white' }}>
                SIGN UP
              </Link>
            </li>
            <li>
              <Link to="/signin" onMouseEnter={() => speakWithDelay("Go to sign in")} onClick={() => speakWithDelay("You are in sign in")} aria-label="Sign in to your account" role="link" style={{ color: 'white' }}>
                SIGN IN
              </Link>
            </li>
          </ul>
        </nav>
      )}
    </div>
  );
}

export default Navigation;
