import React, { useState, useEffect } from 'react';
import './AccessibleForm.css';

const AccessibleForm = ({ formType }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
  });
  const [formErrors, setFormErrors] = useState({});
  const [feedbackMessage, setFeedbackMessage] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const speakWithDelay = (text) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      speechSynthesis.cancel();
      setTimeout(() => {
        speechSynthesis.speak(utterance);
      }, 100);
    } else {
      console.error("Your browser does not support speech synthesis.");
    }
  };

  useEffect(() => {
    setFormData({
      name: '',
      email: '',
      password: '',
      confirmPassword: '',
    });
    setFormErrors({});
    setIsSubmitting(false);
  }, [formType]);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const errors = validate(formData);
    setFormErrors(errors);

    if (Object.keys(errors).length === 0) {
      setIsSubmitting(true);
      const endpoint = formType === 'signup' ? '/signup' : '/signin';

      try {
        const response = await fetch(`http://localhost:5000${endpoint}`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(formData),
        });

        if (response.ok) {
          const data = await response.json();
          setFeedbackMessage(data.message);
          speak(data.message);
          setFormData({ name: '', email: '', password: '', confirmPassword: '' });
        } else {
          const data = await response.json();
          setFeedbackMessage(data.error || data.message);
          speak(data.error || data.message);
        }
      } catch (error) {
        console.error('Error:', error);
        speak("An error occurred. Please try again.");
      }

      setIsSubmitting(false);
    } else {
      Object.values(errors).forEach(error => speakWithDelay(error));
    }
  };

  const validate = (values) => {
    const errors = {};
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (!values.email) {
      errors.email = 'Email is required';
    } else if (!emailRegex.test(values.email)) {
      errors.email = 'Email is invalid';
    }
    if (!values.password) {
      errors.password = 'Password is required';
    }
    if (formType === 'signup') {
      if (!values.name) {
        errors.name = 'Name is required';
      }
      if (values.password !== values.confirmPassword) {
        errors.confirmPassword = 'Passwords do not match';
      }
    }
    return errors;
  };

  return (
    <form onSubmit={handleSubmit} aria-labelledby="formHeading" noValidate>
      <h1 id="formHeading">{formType === 'signup' ? 'SIGN UP' : 'SIGN IN'}</h1>
      <div className="feedbackMessage">{feedbackMessage}</div>
      {formType === 'signup' && (
        <>
          <label htmlFor="name">Name</label>
          <input type="text" id="name" name="name" value={formData.name} onChange={handleChange} aria-describedby="nameError" aria-invalid={!!formErrors.name} required />
          {formErrors.name && <div id="nameError" className="form__error">{formErrors.name}</div>}
        </>
      )}
      <label htmlFor="email">Email</label>
      <input type="email" id="email" name="email" value={formData.email} onChange={handleChange} aria-describedby="emailError" aria-invalid={!!formErrors.email} required />
      {formErrors.email && <div id="emailError" className="form__error">{formErrors.email}</div>}
      <label htmlFor="password">Password</label>
      <input type="password" id="password" name="password" value={formData.password} onChange={handleChange} aria-describedby="passwordError" aria-invalid={!!formErrors.password}required />
{formErrors.password && <div id="passwordError" className="form__error">{formErrors.password}</div>}

{formType === 'signup' && (
<>
<label htmlFor="confirmPassword">Confirm Password</label>
<input
   type="password"
   id="confirmPassword"
   name="confirmPassword"
   value={formData.confirmPassword}
   onChange={handleChange}
   aria-describedby="confirmPasswordError"
   aria-invalid={!!formErrors.confirmPassword}
   required
 />
{formErrors.confirmPassword && (
<div id="confirmPasswordError" className="form__error">
{formErrors.confirmPassword}
</div>
)}
</>
)}

<button>
onMouseEnter={() => speakWithDelay(formType === 'signup' ? "Confirm to sign up" : "Confirm to sign in")}
type="submit"

{formType === 'signup' ? 'Sign Up' : 'Sign In'}
</button>

</form>
);
};
export default AccessibleForm;
