import React, { useState, useEffect, useRef } from 'react';
import './ChatBot.css';


function ChatBot() {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState('');
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }

  useEffect(scrollToBottom, [messages]);

  const handleUserInput = (event) => {
    setUserInput(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (!userInput.trim()) return;

    const newUserMessage = {
      text: userInput,
      author: 'user',
    };

    setMessages([...messages, newUserMessage]);
    setUserInput('');
    respondToUserInput(newUserMessage.text);
  };

  const respondToUserInput = (input) => {
    // This is where you'd add more complex logic or integration with a backend service
    const botResponse = {
      text: `You said: ${input}`,
      author: 'bot',
    };

    setTimeout(() => setMessages(messages => [...messages, botResponse]), 500);
  };

  return (
    <div className="chatbot">
      <div className="messages">
        {messages.map((message, index) => (
          <div key={index} className={`message ${message.author}`}>
            {message.text}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={userInput}
          onChange={handleUserInput}
          placeholder="Ask me something..."
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default ChatBot;
