import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import './Navigation.css';

function Navigation() {
  let location = useLocation();
  let navigate = useNavigate();

  // Assume we use local storage to track if the user is 'logged in'
  const isLoggedIn = localStorage.getItem('userName');

  const handleLogout = () => {
    localStorage.removeItem('userName'); // Clear user-related storage indicating logged out
    navigate('/signin'); // Navigate to the sign-in page
  };

  return (
    <div className="navigation-container">
      {/* Hide navigation links if logged in */}
      {!isLoggedIn && (
        <nav>
          <ul>
            <li>
              <Link
                to="/signup"
                onMouseEnter={() => speakWithDelay("Go to sign up")}
                onClick={() => speakWithDelay("You are in sign up")}
                aria-label="Sign up for a new account"
                role="link"
                style={{ color: 'white'}}
              >
                SIGN UP
              </Link>
            </li>
            <li>
              <Link
                to="/signin"
                onMouseEnter={() => speakWithDelay("Go to sign in")}
                onClick={() => speakWithDelay("You are in sign in")}
                aria-label="Sign in to your account"
                role="link"
                style={{ color: 'white'}}
              >
                SIGN IN
              </Link>
            </li>
          </ul>
        </nav>
      )}

      {/* Show logout button if logged in and not on sign-in or sign-up pages */}
      {isLoggedIn && location.pathname !== '/signin' && location.pathname !== '/signup' && (
        <button onClick={handleLogout} className="logout-button">Logout</button>
      )}
    </div>
  );
}

export default Navigation;
