import React from 'react';
import './App.css';
import { useState, useEffect } from 'react';
import AccessibleForm from './AccessibleForm';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';

function App() {
  const [highContrast, setHighContrast] = useState(false);

  useEffect(() => {
    document.body.classList.toggle('high-contrast', highContrast);
  }, [highContrast]);
  return (
    <Router>
      <div className="App">
        <nav>
          <ul>
            <li>
            <Link to="/signup" aria-label="Sign up for a new account" style={{ color: 'white', backgroundColor: '#007bff' }}>SIGN UP</Link>
            </li>
            <li>
            <Link to="/signin" aria-label="Sign up for a new account" style={{ color: 'white', backgroundColor: '#007bff' }}>SIGN IN</Link>
            </li>
          </ul>
        </nav>
        {/* Routes replaces Switch in v6 */}
        <Routes>
          <Route path="/signup" element={<AccessibleForm formType="signup" />} />
          <Route path="/signin" element={<AccessibleForm formType="signin" />} />
        </Routes>
        <button onClick={() => setHighContrast(!highContrast)}>
          Toggle High Contrast
        </button>
      </div>
    </Router>
  );
}

export default App;
