import React, { useState, useEffect } from 'react';
import AccessibleForm from './AccessibleForm';
import { BrowserRouter as Router, Route, Routes, Link, useLocation } from 'react-router-dom';
import WelcomePage from './WelcomePage'; 
import Navigation from './Navigation'; 
import './App.css';


function App() {
  const [highContrast, setHighContrast] = useState(false);

  useEffect(() => {
    document.body.classList.toggle('high-contrast', highContrast);
  }, [highContrast]);

  const handleClick = () => {
    // Toggle contrast mode
    setHighContrast(!highContrast);

    // Speak the current contrast mode
    const contrastMode = highContrast ? 'Disable High Contrast' : 'Enable High Contrast';
    speakWithDelay(contrastMode);
  };

  const speakWithDelay = (text) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      speechSynthesis.cancel(); // Clear any previously scheduled speech synthesis
      setTimeout(() => {
        speechSynthesis.speak(utterance); // Delayed speech synthesis
      }, 100); // Adjust the delay time as needed
    } else {
      console.error("Your browser does not support speech synthesis.");
    }
  };

  return (
    <Router>
      <div className="App">
      <Navigation /> {/* Use the Navigation component */}
        <nav>
          <ul>
            <li>
              <Link
                to="/signup"
                onMouseEnter={() => speakWithDelay("Go to sign up")}
                onClick={() => speakWithDelay("You are in sign up")}
                aria-label="Sign up for a new account"
                role="link"
                style={{ color: 'white'}}
              >
                SIGN UP
              </Link>
            </li>
            <li>
              <Link
                to="/signin"
                onMouseEnter={() => speakWithDelay("Go to sign in")}
                onClick={() => speakWithDelay("You are in sign in")}
                aria-label="Sign in to your account"
                role="link"
                style={{ color: 'white'}}
              >
                SIGN IN
              </Link>
            </li>
          </ul>
        </nav>
        <Routes>
          <Route path="/signup" element={<AccessibleForm formType="signup" />} />
          <Route path="/signin" element={<AccessibleForm formType="signin" />} />
          <Route path="/welcome" element={<WelcomePage />} />
        </Routes>
        <button
          onMouseEnter={() => speakWithDelay("Toggle Contrast")}
          className="toggle-contrast"
          onClick={handleClick}
          aria-pressed={highContrast}
          aria-label="Toggle high contrast mode"
        >
          {highContrast ? 'Disable High Contrast' : 'Enable High Contrast'}
        </button>
      </div>
    </Router>
  );
}

export default App;
