import React from 'react';
import './AccessibleForm.css';

const AccessibleForm = ({ formType }) => {
  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle form submission logic here
    alert('Form submitted!');
  };

  return (
    <form onSubmit={handleSubmit} aria-labelledby="formHeading">
      <h2 id="formHeading">{formType === 'signup' ? 'Sign Up' : 'Sign In'}</h2>
      {formType === 'signup' && (
        <div>
          <label htmlFor="name">Name:</label>
          <input type="text" id="name" name="name" required />
        </div>
      )}
      <div>
        <label htmlFor="email">Email:</label>
        <input type="email" id="email" name="email" required />
      </div>
      <div>
        <label htmlFor="password">Password:</label>
        <input type="password" id="password" name="password" required />
      </div>
      {formType === 'signup' && (
        <div>
          <label htmlFor="confirmPassword">Confirm Password:</label>
          <input type="password" id="confirmPassword" name="confirmPassword" required />
        </div>
      )}
      <button type="submit">{formType === 'signup' ? 'Sign Up' : 'Sign In'}</button>
    </form>
  );
};

export default AccessibleForm;
