import React, { useState, useEffect } from 'react';
import './AccessibleForm.css';
import { useNavigate } from 'react-router-dom';


const AccessibleForm = ({ formType }) => {

  useEffect(() => {
    // Function to speak text
    const speak = (text) => {
      if ('speechSynthesis' in window) {
        const utterance = new SpeechSynthesisUtterance(text);
        speechSynthesis.cancel(); // Cancel any previous speech
        speechSynthesis.speak(utterance);
      }
    };

    // Speak welcome message once when the component mounts
    speak("Welcome");

  }, []); 

  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
  });
  const [formErrors, setFormErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const speakWithDelay = (text) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      speechSynthesis.cancel(); // Clear any previously scheduled speech synthesis
      setTimeout(() => {
        speechSynthesis.speak(utterance); // Delayed speech synthesis
      }, 100); // Adjust the delay time as needed
    } else {
      console.error("Your browser does not support speech synthesis.");
    }
  };
  // Clear form data and errors when formType changes
  useEffect(() => {
    // Reset form data, errors, and submission state when formType changes
    setFormData({
      name: '',
      email: '',
      password: '',
      confirmPassword: '',
    });
    setFormErrors({});
    setIsSubmitting(false); // Reset isSubmitting to false on form type change
  }, [formType]);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (event) => {
  event.preventDefault();

  const errors = validate(formData);
  setFormErrors(errors);

  if (Object.keys(errors).length === 0) {
    setIsSubmitting(true);

    // Determine the correct endpoint based on the formType
    const endpoint = formType === 'signup' ? '/signup' : '/signin';

    // Use fetch to send a POST request to your server
    try {
      const response = await fetch(`http://localhost:5000${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        console.log(data);
        // Assuming the response includes the user's name
        localStorage.setItem('userName', data.name); // Storing the user's name
        speak(formType === 'signup' ? "Sign Up Successful" : "Sign In Successful");
        navigate('/welcome'); // Redirecting to the welcome page
      }else {
        // Handle server-side validation errors (if any)
        speak("An error occurred, please check your information and try again. This email might already be in use.");
      }
    } catch (error) {
      console.error('Error:', error);
      speak("An error occurred. Please try again.");
    }

    setIsSubmitting(false);
  } else {
    // Speak all error messages
    Object.values(errors).forEach(error => speakWithDelay(error));
  }
};

  
  

  useEffect(() => {
    if (Object.keys(formErrors).length === 0 && isSubmitting) {
      // Handle form submission logic here
      setIsSubmitting(false);
    }
  }, [formErrors]);

  const validate = (values) => {
    const errors = {};
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/; // Simple regex for email validation

    if (!values.email) {
      errors.email = 'Email is required';
    } else if (!emailRegex.test(values.email)) {
      errors.email = 'Email is invalid';
    }
    if (!values.password) {
      errors.password = 'Password is required';
    }
    if (formType === 'signup') {
      if (!values.name) {
        errors.name = 'Name is required';
      }
      if (values.password !== values.confirmPassword) {
        errors.confirmPassword = 'Passwords do not match';
      }
    }
    return errors;
  };
  const speak = (text) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      speechSynthesis.speak(utterance);
    } else {
      console.error("Your browser does not support speech synthesis.");
    }
  };
  

  useEffect(() => {
    if (isSubmitting) {
      const firstErrorField = Object.keys(formErrors)[0];
      if (firstErrorField) {
        document.getElementById(firstErrorField).focus();
      }
    }
  }, [formErrors, isSubmitting]);

  return (
    <form onSubmit={handleSubmit} aria-labelledby="formHeading" noValidate>
      <h1 id="formHeading">{formType === 'signup' ? 'SIGN UP' : 'SIGN IN'}</h1>
      {formType === 'signup' && (
        <div>
          <label htmlFor="name">Name</label>
          <input
            onMouseEnter={() => speakWithDelay("Click to enter your name")}
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            onFocus={() => speak("Please enter your name.")}
            aria-describedby="nameError"
            aria-invalid={!!formErrors.name}
            required
          />
          {formErrors.name && (
            <div id="nameError" className="form__error">
              {formErrors.name}
            </div>
          )}
        </div>
      )}
      <div>
        <label htmlFor="email">Email</label>
        <input
          onMouseEnter={() => speakWithDelay("Click to enter your email")}
          type="email"
          id="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          onFocus={() => speak("Please enter your email.")}
          aria-describedby="emailError"
          aria-invalid={!!formErrors.email}
          required
        />
        {formErrors.email && (
          <div id="emailError" className="form__error">
            {formErrors.email}
          </div>
        )}
      </div>
      <div>
        <label htmlFor="password">Password</label>
        <input
          onMouseEnter={() => speakWithDelay("Click to enter your password")}
          type="password"
          id="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          onFocus={() => speak("Please enter your password.")}
          aria-describedby="passwordError"
          aria-invalid={!!formErrors.password}
          required
        />
        {formErrors.password && (
          <div id="passwordError" className="form__error">
            {formErrors.password}
          </div>
        )}
      </div>
      {formType === 'signup' && (
        <div>
          <label htmlFor="confirmPassword">Confirm Password</label>
          <input            
            onMouseEnter={() => speakWithDelay("Click to confirm your password")}
            type="password"
            id="confirmPassword"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
            onFocus={() => speak("Please enter your password again for confirmation.")}
            aria-describedby="confirmPasswordError"
            aria-invalid={!!formErrors.confirmPassword}
            required
          />
          {formErrors.confirmPassword && (
            <div id="confirmPasswordError" className="form__error">
              {formErrors.confirmPassword}
            </div>
          )}
        </div>
      )}
      <button
        onFocus={() => speakWithDelay("Click to confirm.")}
        onMouseEnter={() => speakWithDelay("Do you want to confirm sign up")}
        type="submit">{formType === 'signup' ? 'SIGN UP' : 'SIGN IN'}</button>
    </form>
  );
};

export default AccessibleForm;
