import React, { useState, useEffect } from 'react';
import './AccessibleForm.css';

const AccessibleForm = ({ formType }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
  });
  const [formErrors, setFormErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const speakWithDelay = (text) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      speechSynthesis.cancel(); // Clear any previously scheduled speech synthesis
      setTimeout(() => {
        speechSynthesis.speak(utterance); // Delayed speech synthesis
      }, 100); // Adjust the delay time as needed
    } else {
      console.error("Your browser does not support speech synthesis.");
    }
  };
  // Clear form data and errors when formType changes
  useEffect(() => {
    // Reset form data, errors, and submission state when formType changes
    setFormData({
      name: '',
      email: '',
      password: '',
      confirmPassword: '',
    });
    setFormErrors({});
    setIsSubmitting(false); // Reset isSubmitting to false on form type change
  }, [formType]);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };
  const AccessibleForm = ({ formType, history }) => {
    const [formData, setFormData] = useState({
      name: '',
      email: '',
      password: '',
      confirmPassword: '',
    });
    const [formErrors, setFormErrors] = useState({});
    const [feedbackMessage, setFeedbackMessage] = useState(''); // For success or error messages
    const [isSubmitting, setIsSubmitting] = useState(false);
  
    // Simplified for brevity
    const handleChange = (event) => {/* ... */};
    const validate = (values) => {/* ... */};
    
    const handleSubmit = async (event) => {
      event.preventDefault();
      const errors = validate(formData);
      setFormErrors(errors);
      
      if (Object.keys(errors).length === 0) {
        setIsSubmitting(true);
        const endpoint = formType === 'signup' ? '/signup' : '/signin';
  
        try {
          const response = await fetch(`http://localhost:5000${endpoint}`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(formData),
          });
          const data = await response.json();
          
          if (response.ok) {
            setFeedbackMessage("Success!");
            speak(formType === 'signup' ? "Sign Up Successful" : "Sign In Successful");
            setFormData({ name: '', email: '', password: '', confirmPassword: '' }); // Reset form
            // Optionally redirect the user or update UI to reflect the successful operation
            // history.push("/dashboard"); // Redirecting with React Router, for example
          } else {
            setFeedbackMessage(data.error || data.message);
          }
        } catch (error) {
          setFeedbackMessage("An error occurred. Please try again.");
        }
        setIsSubmitting(false);
      }
    };
  
    return (
      <form onSubmit={handleSubmit} aria-labelledby="formHeading" noValidate>
        {/* Form fields */}
        <div className="feedbackMessage">{feedbackMessage}</div> {/* Display feedback messages */}
        <button type="submit">{formType === 'signup' ? 'Sign Up' : 'Sign In'}</button>
      </form>
    );
  };
  

  
  
  

  useEffect(() => {
    if (Object.keys(formErrors).length === 0 && isSubmitting) {
      // Handle form submission logic here
      alert('Form submitted!');
      setIsSubmitting(false);
    }
  }, [formErrors]);

  const validate = (values) => {
    const errors = {};
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/; // Simple regex for email validation

    if (!values.email) {
      errors.email = 'Email is required';
    } else if (!emailRegex.test(values.email)) {
      errors.email = 'Email is invalid';
    }
    if (!values.password) {
      errors.password = 'Password is required';
    }
    if (formType === 'signup') {
      if (!values.name) {
        errors.name = 'Name is required';
      }
      if (values.password !== values.confirmPassword) {
        errors.confirmPassword = 'Passwords do not match';
      }
    }
    return errors;
  };
  const speak = (text) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      speechSynthesis.speak(utterance);
    } else {
      console.error("Your browser does not support speech synthesis.");
    }
  };
  

  useEffect(() => {
    if (isSubmitting) {
      const firstErrorField = Object.keys(formErrors)[0];
      if (firstErrorField) {
        document.getElementById(firstErrorField).focus();
      }
    }
  }, [formErrors, isSubmitting]);

  return (
    <form onSubmit={handleSubmit} aria-labelledby="formHeading" noValidate>
      <h1 id="formHeading">{formType === 'signup' ? 'SIGN UP' : 'SIGN IN'}</h1>
      {formType === 'signup' && (
        <div>
          <label htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            onFocus={() => speak("Please enter your name.")}
            aria-describedby="nameError"
            aria-invalid={!!formErrors.name}
            required
          />
          {formErrors.name && (
            <div id="nameError" className="form__error">
              {formErrors.name}
            </div>
          )}
        </div>
      )}
      <div>
        <label htmlFor="email">Email</label>
        <input
          type="email"
          id="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          onFocus={() => speak("Please enter your email address.")}
          aria-describedby="emailError"
          aria-invalid={!!formErrors.email}
          required
        />
        {formErrors.email && (
          <div id="emailError" className="form__error">
            {formErrors.email}
          </div>
        )}
      </div>
      <div>
        <label htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          onFocus={() => speak("Please enter your password.")}
          aria-describedby="passwordError"
          aria-invalid={!!formErrors.password}
          required
        />
        {formErrors.password && (
          <div id="passwordError" className="form__error">
            {formErrors.password}
          </div>
        )}
      </div>
      {formType === 'signup' && (
        <div>
          <label htmlFor="confirmPassword">Confirm Password</label>
          <input
            type="password"
            id="confirmPassword"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
            onFocus={() => speak("Please enter your password again for confirmation.")}
            aria-describedby="confirmPasswordError"
            aria-invalid={!!formErrors.confirmPassword}
            required
          />
          {formErrors.confirmPassword && (
            <div id="confirmPasswordError" className="form__error">
              {formErrors.confirmPassword}
            </div>
          )}
        </div>
      )}
      <button 
        onMouseEnter={() => speakWithDelay("Do you want to confirm sign up")}
        type="submit">{formType === 'signup' ? 'SIGN UP' : 'SIGN IN'}</button>
    </form>
  );
};

export default AccessibleForm;
